import React, { useState, useRef, useEffect } from 'react';
import { useUser } from './App';
import axios from 'axios';
import { useSearchParams } from 'react-router-dom';

const API = axios.create({ 
  baseURL: process.env.REACT_APP_API_URL || 'http://localhost:4000', 
  withCredentials: true 
});

// Add request interceptor for debugging
API.interceptors.request.use(config => {
  const token = localStorage.getItem('jwt');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
    console.log('API request with token:', token.substring(0, 20) + '...');
  } else {
    console.log('API request without token');
  }
  console.log('API request:', config.method?.toUpperCase(), config.url);
  return config;
});

// Add response interceptor for debugging
API.interceptors.response.use(
  response => {
    console.log('API response success:', response.status, response.config.url);
    return response;
  },
  error => {
    console.error('API response error:', error.response?.status, error.response?.data, error.config?.url);
    return Promise.reject(error);
  }
);

const getLanguageLabel = (code) => {
  const languages = {
    'en': 'English',
    'es': 'Spanish', 
    'hi': 'Hindi',
    'ja': 'Japanese',
    'tl': 'Tagalog',
    'ta': 'Tamil',
    'ar': 'Arabic',
    'zh': 'Mandarin',
    'ko': 'Korean'
  };
  return languages[code] || 'English';
};

function usePersistentChatHistory(user) {
  const [chatHistory, setChatHistory] = useState(() => {
    if (!user) {
      const saved = localStorage.getItem('chatHistory');
      return saved ? JSON.parse(saved) : [];
    }
    return [];
  });

  useEffect(() => {
    if (!user) {
      localStorage.setItem('chatHistory', JSON.stringify(chatHistory));
    } else {
      localStorage.removeItem('chatHistory');
    }
  }, [chatHistory, user]);

  return [chatHistory, setChatHistory];
}

function Analyze() {
  const { user } = useUser();
  const [searchParams] = useSearchParams();
  const urlLang = searchParams.get('lang');
  const urlConversationId = searchParams.get('conversation');
  const [isRecording, setIsRecording] = useState(false);
  const [isProcessing, setIsProcessing] = useState(false);
  const [feedback, setFeedback] = useState('');
  const [isLoadingFeedback, setIsLoadingFeedback] = useState(false);
  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);
  const [showSavePrompt, setShowSavePrompt] = useState(false);
  const [chatHistory, setChatHistory] = usePersistentChatHistory(user);
  const [language, setLanguage] = useState(urlLang || user?.target_language || 'en');

  // New conversation management state
  const [conversationId, setConversationId] = useState(urlConversationId ? parseInt(urlConversationId) : null);
  const [isCreatingConversation, setIsCreatingConversation] = useState(false);
  const [isLoadingConversation, setIsLoadingConversation] = useState(false);

  useEffect(() => {
    if (user && localStorage.getItem('chatHistory')) {
      setShowSavePrompt(true);
    }
  }, [user]);

  useEffect(() => {
    if (user?.language && !language) {
      setLanguage(user.language);
    }
  }, [user, language]);

  // Debug chat history changes
  useEffect(() => {
    console.log('Chat history changed:', chatHistory);
  }, [chatHistory]);

  // Handle conversation loading and creation
  useEffect(() => {
    console.log('Conversation effect triggered:', { 
      user: user ? user.email : 'no user', 
      conversationId, 
      isCreatingConversation,
      urlConversationId
    });

    if (user) {
      if (urlConversationId && !conversationId && !isLoadingConversation) {
        // Load existing conversation from URL
        console.log('Loading existing conversation from URL:', urlConversationId);
        loadExistingConversation(parseInt(urlConversationId));
      } else if (!conversationId && !isCreatingConversation && !isLoadingConversation) {
        // Create new conversation
        console.log('Creating new conversation...');
        createNewConversation();
      }
    }
  }, [user, conversationId, isCreatingConversation, isLoadingConversation, urlConversationId]);

  const createNewConversation = async () => {
    if (!user) {
      console.log('No user found, skipping conversation creation');
      return;
    }

    console.log('Creating new conversation for user:', user.email);
    setIsCreatingConversation(true);
    try {
      const title = `${getLanguageLabel(language)} Practice Session`;
      console.log('Sending conversation creation request:', { language, title });
      const response = await API.post('/api/conversations', {
        language,
        title
      });
      console.log('Conversation creation response:', response.data);
      setConversationId(response.data.conversation.id);
      console.log('Created new conversation:', response.data.conversation.id);
    } catch (error) {
      console.error('Error creating conversation:', error);
      console.error('Error details:', error.response?.data || error.message);
    } finally {
      setIsCreatingConversation(false);
    }
  };

  const loadExistingConversation = async (convId) => {
    if (!user || !convId) {
      console.log('No user or conversation ID, skipping load');
      return;
    }

    console.log('Loading existing conversation:', convId);
    setIsLoadingConversation(true);
    try {
      const response = await API.get(`/api/conversations/${convId}`);
      console.log('Conversation load response:', response.data);

      const conversation = response.data.conversation;
      setConversationId(conversation.id);
      setLanguage(conversation.language);

      // Convert messages to chat history format
      const messages = conversation.messages || [];
      const history = messages.map(msg => ({
        sender: msg.sender,
        text: msg.text,
        timestamp: new Date(msg.created_at)
      }));

      console.log('Loaded conversation history:', history);
      setChatHistory(history);
    } catch (error) {
      console.error('Error loading conversation:', error);
      console.error('Error details:', error.response?.data || error.message);
    } finally {
      setIsLoadingConversation(false);
    }
  };

  const saveMessageToBackend = async (sender, text, messageType = 'text', audioFilePath = null) => {
    if (!conversationId) {
      console.error('No conversation ID available');
      return;
    }

    console.log('Saving message to backend:', { sender, text, conversationId });
    try {
      const response = await API.post(`/api/conversations/${conversationId}/messages`, {
        sender,
        text,
        messageType,
        audioFilePath
      });
      console.log('Message saved to backend successfully:', response.data);
    } catch (error) {
      console.error('Error saving message to backend:', error);
      console.error('Error details:', error.response?.data || error.message);
    }
  };

  const resetConversation = () => {
    setChatHistory([]);
    setFeedback('');
    setConversationId(null); // This will trigger creation of a new conversation

    // Update URL to remove conversation parameter
    const newUrl = new URL(window.location);
    newUrl.searchParams.delete('conversation');
    window.history.replaceState({}, '', newUrl);
  };

  // Remove old session save logic - no longer needed
  const saveSessionToBackend = async (showAlert = true) => {
    // This function is kept for compatibility but no longer needed
    // Messages are now saved individually as they are sent
    if (showAlert) {
      alert('Messages are automatically saved to your account!');
    }
  };

  // Remove old auto-save logic - messages are saved individually now
  const autoSaveSession = async () => {
    // This function is kept for compatibility but no longer needed
    // Messages are now saved individually as they are sent
  };

  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      mediaRecorderRef.current = new MediaRecorder(stream);
      audioChunksRef.current = [];

      mediaRecorderRef.current.ondataavailable = (event) => {
        audioChunksRef.current.push(event.data);
      };

      mediaRecorderRef.current.onstop = async () => {
        const audioBlob = new Blob(audioChunksRef.current, { type: 'audio/wav' });
        await sendAudioToBackend(audioBlob);
      };

      mediaRecorderRef.current.start();
      setIsRecording(true);
    } catch (error) {
      console.error('Error starting recording:', error);
      alert('Error accessing microphone. Please check permissions.');
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current && isRecording) {
      mediaRecorderRef.current.stop();
      mediaRecorderRef.current.stream.getTracks().forEach(track => track.stop());
      setIsRecording(false);
    }
  };

  const sendAudioToBackend = async (audioBlob) => {
    setIsProcessing(true);
    const userMessage = { sender: 'User', text: 'üé§ Recording...', timestamp: new Date() };
    setChatHistory(prev => [...prev, userMessage]);

    try {
      console.log('=== FRONTEND: Starting audio processing ===');
      const formData = new FormData();
      formData.append('audio', audioBlob, 'recording.wav');
      formData.append('chatHistory', JSON.stringify(chatHistory));
      formData.append('language', language);

      console.log('=== FRONTEND: Sending request to server ===');
      const response = await API.post('/api/analyze', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      console.log('=== FRONTEND: Server response received ===');
      console.log('Server response:', response.data);

      console.log('=== FRONTEND: Processing transcription ===');
      const transcription = response.data.transcription || 'Speech recorded';
      console.log('Transcription:', transcription);

      console.log('=== FRONTEND: Updating chat history with transcription ===');
      setChatHistory(prev => prev.map(msg => 
        msg === userMessage ? { ...msg, text: transcription } : msg
      ));

      // Save user message to backend
      await saveMessageToBackend('User', transcription, 'text');

      if (response.data.aiResponse) {
        console.log('=== FRONTEND: Processing AI response ===');
        console.log('AI Response received:', response.data.aiResponse);
        const aiMessage = { 
          sender: 'AI', 
          text: response.data.aiResponse, 
          timestamp: new Date() 
        };
        console.log('=== FRONTEND: Adding AI message to chat history ===');
        setChatHistory(prev => {
          const newHistory = [...prev, aiMessage];
          console.log('Updated chat history:', newHistory);
          return newHistory;
        });

        // Save AI message to backend
        await saveMessageToBackend('AI', response.data.aiResponse, 'text');
      } else {
        console.log('No AI response received');
      }

      if (response.data.ttsUrl) {
        console.log('=== FRONTEND: Processing TTS ===');
        console.log('TTS URL received:', response.data.ttsUrl);
        const audioUrl = `http://localhost:4000${response.data.ttsUrl}`;
        console.log('Full audio URL:', audioUrl);

        // Test if the audio file exists by making a HEAD request
        try {
          console.log('=== FRONTEND: Checking TTS audio file ===');
          const headResponse = await fetch(audioUrl, { method: 'HEAD' });
          if (!headResponse.ok) {
            console.error('TTS audio file not found:', headResponse.status);
            console.log('Skipping TTS playback due to missing file');
          } else {
            console.log('TTS audio file exists, size:', headResponse.headers.get('content-length'));

            console.log('=== FRONTEND: Creating audio element ===');
            const audio = new window.Audio(audioUrl);

            // Add error handling for audio playback
            audio.onerror = (e) => {
              console.error('TTS audio playback error:', e);
              console.error('Audio error details:', audio.error);
            };

            audio.onloadstart = () => {
              console.log('TTS audio loading started');
            };

            audio.oncanplay = () => {
              console.log('TTS audio can play');
            };

            audio.onended = () => {
              console.log('TTS audio playback completed');
            };

            audio.onload = () => {
              console.log('TTS audio loaded successfully');
            };

            // Try to play the audio
            console.log('=== FRONTEND: Attempting to play TTS audio ===');
            audio.play().catch(error => {
              console.error('Failed to play TTS audio:', error);
            });
            console.log('=== FRONTEND: TTS audio play() called ===');
          }
        } catch (fetchError) {
          console.error('Error checking TTS audio file:', fetchError);
          console.log('Skipping TTS playback due to fetch error');
        }
      } else {
        console.log('No TTS URL received from server');
      }

      console.log('=== FRONTEND: All processing complete, should be successful ===');
    } catch (error) {
      console.error('=== FRONTEND: ERROR CAUGHT ===');
      console.error('Error type:', error.constructor.name);
      console.error('Error message:', error.message);
      console.error('Error stack:', error.stack);
      console.error('Full error object:', error);

      if (error.response) {
        console.error('Server responded with error:', error.response.status, error.response.data);
      } else if (error.request) {
        console.error('No response received:', error.request);
      } else {
        console.error('Error setting up request:', error.message);
      }

      const errorMessage = { 
        sender: 'System', 
        text: '‚ùå Error processing audio. Please try again.', 
        timestamp: new Date() 
      };
      setChatHistory(prev => [...prev, errorMessage]);
    } finally {
      console.log('=== FRONTEND: Finally block - setting isProcessing to false ===');
      setIsProcessing(false);
    }
  };

  const requestDetailedFeedback = async () => {
    if (chatHistory.length === 0) {
      alert('Please record some speech first.');
      return;
    }
    setIsLoadingFeedback(true);
    try {
      const response = await API.post('/api/feedback', {
        chatHistory: chatHistory,
        language,
      });
      setFeedback(response.data.feedback);
    } catch (error) {
      console.error('Error requesting feedback:', error);
      setFeedback('‚ùå Error getting detailed feedback. Please try again.');
    } finally {
      setIsLoadingFeedback(false);
    }
  };

  return (
    <div style={{ 
      display: 'flex', 
      height: 'calc(100vh - 80px)', 
      background: '#f5f1ec',
      padding: '2rem'
    }}>
      <div style={{ flex: 1, background: '#fff', borderRadius: 0, marginRight: '1rem', display: 'flex', flexDirection: 'column', border: '1px solid #e0e0e0', boxShadow: '0 1px 4px rgba(0,0,0,0.02)' }}>
        {/* Reset Conversation Button */}
        <div style={{ padding: '1rem', background: '#f5f1ec', borderBottom: '1px solid #ececec', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <div style={{ color: '#7e5a75', fontWeight: 600, fontSize: '0.9rem' }}>
            Language: {getLanguageLabel(language)}
          </div>
          <button onClick={resetConversation} style={{
            fontSize: '1rem',
            padding: '0.4rem 1.2rem',
            borderRadius: 6,
            border: '1px solid #c38d94',
            background: '#fff',
            color: '#c38d94',
            fontWeight: 600,
            cursor: 'pointer',
            marginRight: 8
          }}>
            üîÑ Reset Conversation
          </button>
          {/* Language display (not dropdown) */}
          {language && (
            <span style={{ fontWeight: 600, color: '#3c4c73', marginLeft: 16 }}>
              üåê Language: {getLanguageLabel(language)}
            </span>
          )}
        </div>
        {/* Save session prompt - updated for new system */}
        {showSavePrompt && (
          <div style={{ background: '#fffbe6', border: '1px solid #c38d94', padding: 16, borderRadius: 8, margin: 16 }}>
            <p>You have an unsaved session. Save it to your account?</p>
            <button onClick={saveSessionToBackend} style={{ marginRight: 8 }}>Save</button>
            <button onClick={() => { setShowSavePrompt(false); localStorage.removeItem('chatHistory'); }}>Dismiss</button>
          </div>
        )}
        {/* Chat Header */}
        <div style={{ background: '#3c4c73', color: '#fff', padding: '1rem', borderRadius: '18px 18px 0 0', textAlign: 'center' }}>
          <h2>üé§ BeyondWords Chat</h2>
          {conversationId && (
            <div style={{ fontSize: '0.8rem', opacity: 0.8, marginTop: '0.5rem' }}>
              Conversation ID: {conversationId}
            </div>
          )}
        </div>
        {/* Chat Messages */}
        <div style={{ 
          flex: 1, 
          padding: '1rem', 
          overflowY: 'auto',
          display: 'flex',
          flexDirection: 'column',
          gap: '0.5rem'
        }}>
          {console.log('Rendering chat history:', chatHistory)}
          {chatHistory.map((message, index) => (
            <div key={index} style={{
              maxWidth: '80%',
              padding: '0.75rem 1rem',
              borderRadius: 0,
              alignSelf: message.sender === 'User' ? 'flex-end' : 'flex-start',
              background: message.sender === 'User' ? '#c38d94' : 
                         message.sender === 'AI' ? '#fff' : '#f5f1ec',
              color: message.sender === 'User' ? '#3e3e3e' : '#7e5a75',
              border: message.sender === 'AI' ? '1px solid #c38d94' : 'none',
              fontSize: '0.9rem'
            }}>
              {message.text}
            </div>
          ))}
          {isProcessing && (
            <div style={{
              alignSelf: 'center',
              padding: '0.5rem 1rem',
              background: '#f5f1ec',
              borderRadius: 0,
              color: '#7e5a75',
              fontSize: '0.9rem'
            }}>
              ‚è≥ Processing your speech...
            </div>
          )}
        </div>
        {/* Recording Controls */}
        <div style={{ 
          padding: '1rem', 
          borderTop: '1px solid #c38d94',
          background: '#f5f1ec',
          borderRadius: '0 0 18px 18px',
          textAlign: 'center'
        }}>
          <button
            onClick={isRecording ? stopRecording : startRecording}
            disabled={isProcessing}
            style={{
              width: 60,
              height: 60,
              borderRadius: '50%',
              border: 'none',
              background: isRecording ? '#7e5a75' : '#c38d94',
              color: isRecording ? '#fff' : '#3e3e3e',
              fontSize: '24px',
              cursor: isProcessing ? 'not-allowed' : 'pointer',
              transition: 'all 0.3s',
              animation: isRecording ? 'pulse 1.5s infinite' : 'none'
            }}
          >
            {isRecording ? '‚èπÔ∏è' : 'üé§'}
          </button>
          <p style={{ marginTop: '0.5rem', color: '#7e5a75', fontSize: '0.9rem' }}>
            {isRecording ? 'Recording... Click to stop' : 'Click to start recording'}
          </p>
        </div>
      </div>
      {/* Feedback Section */}
      <div style={{ 
        width: 320, 
        background: '#f5f1ec', 
        borderRadius: 0,
        display: 'flex',
        flexDirection: 'column',
        border: '1px solid #e0e0e0',
        boxShadow: '0 1px 4px rgba(0,0,0,0.02)'
      }}>
        {/* Feedback Header */}
        <div style={{ 
          background: '#c38d94', 
          color: '#3e3e3e', 
          padding: '1rem', 
          borderRadius: '18px 18px 0 0',
          textAlign: 'center',
          borderBottom: '1px solid #ececec'
        }}>
          <h3>üìä Detailed Analysis</h3>
        </div>
        {/* Feedback Content */}
        <div style={{ 
          flex: 1, 
          padding: '1rem',
          display: 'flex',
          flexDirection: 'column'
        }}>
          <button
            onClick={requestDetailedFeedback}
            disabled={isLoadingFeedback || chatHistory.length === 0}
            style={{
              width: '100%',
              padding: '0.75rem',
              background: '#3c4c73',
              color: '#fff',
              border: 'none',
              borderRadius: 0,
              boxShadow: 'inset 0 2px 8px #3c4c7322',
              cursor: isLoadingFeedback ? 'not-allowed' : 'pointer',
              fontWeight: 600,
              marginBottom: '1rem'
            }}
          >
            {isLoadingFeedback ? '‚è≥ Processing...' : 'Request Detailed Feedback'}
          </button>
          {feedback && (
            <div style={{
              background: '#fff',
              padding: '1rem',
              borderRadius: 0,
              border: '1px solid #c38d94',
              flex: 1,
              overflowY: 'auto',
              fontSize: '0.9rem',
              lineHeight: 1.5,
              whiteSpace: 'pre-wrap'
            }}>
              {feedback}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default Analyze;
