import React, { useState, useEffect } from 'react';
import { useUser } from './App';
import axios from 'axios';

const TALK_TOPICS = [
  { id: 'family',    label: 'Family and relationships',            icon: '👨‍👩‍👧‍👦' },
  { id: 'travel',    label: 'Travel experiences and cultures',      icon: '✈️' },
  { id: 'heritage',  label: 'Cultural heritage and traditions',     icon: '🏛️' },
  { id: 'business',  label: 'Work and professional life',           icon: '💼' },
  { id: 'media',     label: 'Movies, music, and media',             icon: '🎬' },
  { id: 'food',      label: 'Food and cooking',                     icon: '🍽️' },
  { id: 'hobbies',   label: 'Hobbies and leisure activities',       icon: '🎨' },
  { id: 'news',      label: 'News and current events',              icon: '📰' },
  { id: 'sports',    label: 'Sports and fitness',                   icon: '⚽️' },
  { id: 'education', label: 'Education and learning',               icon: '📚' },
  { id: 'technology', label: 'Technology and innovation',           icon: '💻' },
  { id: 'health',    label: 'Health and wellness',                  icon: '🏥' }
];

const PROFICIENCY_LEVELS = [
  { level: 'beginner', label: 'Beginner', icon: '🌱', description: 'I know some basic words and phrases.' },
  { level: 'elementary', label: 'Elementary', icon: '🌿', description: 'I can have simple conversations on familiar topics.' },
  { level: 'intermediate', label: 'Intermediate', icon: '🌳', description: 'I can discuss familiar topics, understand main points, and ask questions.' },
  { level: 'advanced', label: 'Advanced', icon: '🏔️', description: 'I can express ideas fluently and understand complex topics.' },
  { level: 'fluent', label: 'Fluent', icon: '🗝️', description: 'I can communicate naturally and understand nuanced conversations.' }
];

const LANGUAGES = [
  { code: 'es', label: 'Spanish', flag: '🇪🇸', description: 'Español' },
  { code: 'hi', label: 'Hindi', flag: '🇮🇳', description: 'हिन्दी' },
  { code: 'ja', label: 'Japanese', flag: '🇯🇵', description: '日本語' },
  { code: 'tl', label: 'Tagalog', flag: '🇵🇭', description: 'Filipino' },
  { code: 'ta', label: 'Tamil', flag: '🇮🇳', description: 'தமிழ்' },
  { code: 'ar', label: 'Arabic', flag: '🇸🇦', description: 'العربية' },
  { code: 'zh', label: 'Mandarin', flag: '🇨🇳', description: '中文' },
  { code: 'ko', label: 'Korean', flag: '🇰🇷', description: '한국어' }
];

const API = axios.create({ 
  baseURL: process.env.REACT_APP_API_URL || 'http://localhost:4000', 
  withCredentials: false 
});

API.interceptors.request.use(config => {
  const token = localStorage.getItem('jwt');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

function Profile() {
  const { user, setUser } = useUser();
  const [isLoading, setIsLoading] = useState(false);
  const [isSaving, setIsSaving] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // Form states
  const [profileData, setProfileData] = useState({
    name: '',
    email: '',
    target_language: '',
    proficiency_level: '',
    talk_topics: []
  });

  useEffect(() => {
    if (user) {
      console.log('Setting profile data from user:', user);
      setProfileData({
        name: user.name || '',
        email: user.email || '',
        target_language: user.target_language || '',
        proficiency_level: user.proficiency_level || '',
        talk_topics: user.talk_topics || []
      });
    }
  }, [user]);

  const handleInputChange = (field, value) => {
    setProfileData(prev => ({
      ...prev,
      [field]: value
    }));
    setError('');
    setSuccess('');
  };

  const toggleTopic = (topicId) => {
    setProfileData(prev => ({
      ...prev,
      talk_topics: prev.talk_topics.includes(topicId)
        ? prev.talk_topics.filter(id => id !== topicId)
        : [...prev.talk_topics, topicId]
    }));
    setError('');
    setSuccess('');
  };

  const handleSave = async () => {
    setIsSaving(true);
    setError('');
    setSuccess('');

    console.log('Saving profile data:', profileData);

    try {
      const response = await API.put('/api/user/profile', {
        name: profileData.name,
        email: profileData.email,
        target_language: profileData.target_language,
        proficiency_level: profileData.proficiency_level,
        talk_topics: profileData.talk_topics
      });

      console.log('Profile update response:', response.data);
      setUser(response.data.user);
      setSuccess('Profile updated successfully!');
    } catch (err) {
      console.error('Error updating profile:', err);
      console.error('Error response:', err.response?.data);
      setError(err.response?.data?.error || 'Failed to update profile. Please try again.');
    } finally {
      setIsSaving(false);
    }
  };

  const getLanguageInfo = (code) => {
    return LANGUAGES.find(lang => lang.code === code) || { label: code, flag: '🌍' };
  };

  const getProficiencyInfo = (level) => {
    return PROFICIENCY_LEVELS.find(p => p.level === level) || { label: level, icon: '🌱' };
  };

  const getTopicInfo = (id) => {
    return TALK_TOPICS.find(topic => topic.id === id) || { label: id, icon: '💭' };
  };

  if (!user) {
    return (
      <div style={{ 
        minHeight: '100vh', 
        display: 'flex', 
        alignItems: 'center', 
        justifyContent: 'center',
        background: 'linear-gradient(135deg, #f5f1ec 0%, #e8e0d8 50%, #d4c8c0 100%)'
      }}>
        <div style={{ color: '#7e5a75', fontSize: '1.2rem' }}>Please log in to view your profile.</div>
      </div>
    );
  }

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #f5f1ec 0%, #e8e0d8 50%, #d4c8c0 100%)',
      padding: '2rem'
    }}>
      <div style={{ maxWidth: 800, margin: '0 auto' }}>
        {/* Header */}
        <div style={{
          background: '#fff',
          borderRadius: 20,
          boxShadow: '0 8px 32px rgba(60,76,115,0.12)',
          padding: '2.5rem 2rem',
          marginBottom: '2rem'
        }}>
          <h1 style={{
            color: '#3c4c73',
            fontFamily: 'Grandstander, Arial, sans-serif',
            fontWeight: 700,
            fontSize: '2rem',
            marginBottom: '0.5rem'
          }}>
            ⚙️ Profile Settings
          </h1>
          <p style={{ color: '#7e5a75', fontSize: '1rem', margin: 0 }}>
            Manage your account information and learning preferences
          </p>
        </div>

        {/* Messages */}
        {error && (
          <div style={{
            background: 'rgba(220,53,69,0.1)',
            color: '#dc3545',
            padding: '1rem',
            borderRadius: 12,
            marginBottom: '2rem',
            border: '1px solid rgba(220,53,69,0.2)'
          }}>
            {error}
          </div>
        )}

        {success && (
          <div style={{
            background: 'rgba(40,167,69,0.1)',
            color: '#28a745',
            padding: '1rem',
            borderRadius: 12,
            marginBottom: '2rem',
            border: '1px solid rgba(40,167,69,0.2)'
          }}>
            {success}
          </div>
        )}

        {/* Personal Information */}
        <div style={{
          background: '#fff',
          borderRadius: 16,
          boxShadow: '0 4px 20px rgba(60,76,115,0.1)',
          padding: '2rem',
          marginBottom: '2rem'
        }}>
          <h2 style={{
            color: '#3c4c73',
            fontSize: '1.3rem',
            fontWeight: 600,
            marginBottom: '1.5rem',
            display: 'flex',
            alignItems: 'center',
            gap: '0.5rem'
          }}>
            👤 Personal Information
          </h2>

          <div style={{ display: 'grid', gap: '1.5rem' }}>
            <div>
              <label style={{ 
                display: 'block', 
                color: '#3c4c73', 
                fontWeight: 600, 
                marginBottom: '0.5rem' 
              }}>
                Full Name
              </label>
              <input
                type="text"
                value={profileData.name}
                onChange={(e) => handleInputChange('name', e.target.value)}
                style={{
                  width: '100%',
                  padding: '0.75rem',
                  borderRadius: '8px',
                  border: '2px solid rgba(126,90,117,0.2)',
                  fontSize: '1rem',
                  fontFamily: 'inherit',
                  background: '#f8f6f4',
                  color: '#3c4c73'
                }}
              />
            </div>

            <div>
              <label style={{ 
                display: 'block', 
                color: '#3c4c73', 
                fontWeight: 600, 
                marginBottom: '0.5rem' 
              }}>
                Email Address
              </label>
              <input
                type="email"
                value={profileData.email}
                onChange={(e) => handleInputChange('email', e.target.value)}
                style={{
                  width: '100%',
                  padding: '0.75rem',
                  borderRadius: '8px',
                  border: '2px solid rgba(126,90,117,0.2)',
                  fontSize: '1rem',
                  fontFamily: 'inherit',
                  background: '#f8f6f4',
                  color: '#3c4c73'
                }}
              />
            </div>
          </div>
        </div>

        {/* Language Settings */}
        <div style={{
          background: '#fff',
          borderRadius: 16,
          boxShadow: '0 4px 20px rgba(60,76,115,0.1)',
          padding: '2rem',
          marginBottom: '2rem'
        }}>
          <h2 style={{
            color: '#3c4c73',
            fontSize: '1.3rem',
            fontWeight: 600,
            marginBottom: '1.5rem',
            display: 'flex',
            alignItems: 'center',
            gap: '0.5rem'
          }}>
            🌍 Language Settings
          </h2>

          <div style={{ marginBottom: '2rem' }}>
            <label style={{ 
              display: 'block', 
              color: '#3c4c73', 
              fontWeight: 600, 
              marginBottom: '1rem' 
            }}>
              Target Language
            </label>
            <div style={{ 
              display: 'grid', 
              gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', 
              gap: '0.75rem' 
            }}>
              {LANGUAGES.map(lang => (
                <div
                  key={lang.code}
                  onClick={() => handleInputChange('target_language', lang.code)}
                  style={{
                    padding: '1rem',
                    borderRadius: '8px',
                    border: `2px solid ${profileData.target_language === lang.code ? '#7e5a75' : 'rgba(126,90,117,0.2)'}`,
                    backgroundColor: profileData.target_language === lang.code ? 'rgba(126,90,117,0.1)' : '#f8f6f4',
                    cursor: 'pointer',
                    transition: 'all 0.3s ease',
                    display: 'flex',
                    alignItems: 'center',
                    gap: '0.5rem'
                  }}
                >
                  <span style={{ fontSize: '1.5rem' }}>{lang.flag}</span>
                  <div>
                    <div style={{ fontWeight: 600, color: '#3c4c73' }}>{lang.label}</div>
                    <div style={{ fontSize: '0.8rem', color: '#7e5a75' }}>{lang.description}</div>
                  </div>
                </div>
              ))}
            </div>
          </div>

          <div>
            <label style={{ 
              display: 'block', 
              color: '#3c4c73', 
              fontWeight: 600, 
              marginBottom: '1rem' 
            }}>
              Proficiency Level
            </label>
            <div style={{ display: 'flex', flexDirection: 'column', gap: '0.75rem' }}>
              {PROFICIENCY_LEVELS.map(level => (
                <div
                  key={level.level}
                  onClick={() => handleInputChange('proficiency_level', level.level)}
                  style={{
                    padding: '1rem',
                    borderRadius: '8px',
                    border: `2px solid ${profileData.proficiency_level === level.level ? '#7e5a75' : 'rgba(126,90,117,0.2)'}`,
                    backgroundColor: profileData.proficiency_level === level.level ? 'rgba(126,90,117,0.1)' : '#f8f6f4',
                    cursor: 'pointer',
                    transition: 'all 0.3s ease',
                    display: 'flex',
                    alignItems: 'center',
                    gap: '1rem'
                  }}
                >
                  <span style={{ fontSize: '1.5rem' }}>{level.icon}</span>
                  <div>
                    <div style={{ fontWeight: 600, color: '#3c4c73' }}>{level.label}</div>
                    <div style={{ fontSize: '0.9rem', color: '#7e5a75' }}>{level.description}</div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Topic Preferences */}
        <div style={{
          background: '#fff',
          borderRadius: 16,
          boxShadow: '0 4px 20px rgba(60,76,115,0.1)',
          padding: '2rem',
          marginBottom: '2rem'
        }}>
          <h2 style={{
            color: '#3c4c73',
            fontSize: '1.3rem',
            fontWeight: 600,
            marginBottom: '1rem',
            display: 'flex',
            alignItems: 'center',
            gap: '0.5rem'
          }}>
            💭 Favorite Topics
          </h2>
          <p style={{ color: '#7e5a75', marginBottom: '1.5rem', fontSize: '0.9rem' }}>
            Select topics you enjoy discussing. These will be suggested when starting new conversations.
          </p>

          <div style={{ 
            display: 'grid', 
            gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', 
            gap: '0.75rem' 
          }}>
            {TALK_TOPICS.map(topic => (
              <div
                key={topic.id}
                onClick={() => toggleTopic(topic.id)}
                style={{
                  padding: '0.75rem',
                  borderRadius: '8px',
                  border: `2px solid ${profileData.talk_topics.includes(topic.id) ? '#7e5a75' : 'rgba(126,90,117,0.2)'}`,
                  backgroundColor: profileData.talk_topics.includes(topic.id) ? 'rgba(126,90,117,0.1)' : '#f8f6f4',
                  cursor: 'pointer',
                  transition: 'all 0.3s ease',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '0.5rem'
                }}
              >
                <span style={{ fontSize: '1.2rem' }}>{topic.icon}</span>
                <span style={{ fontWeight: 500, color: '#3c4c73', fontSize: '0.9rem' }}>{topic.label}</span>
              </div>
            ))}
          </div>

          {profileData.talk_topics.length > 0 && (
            <div style={{
              marginTop: '1.5rem',
              padding: '1rem',
              background: 'rgba(126,90,117,0.1)',
              borderRadius: '8px'
            }}>
              <h4 style={{ color: '#3c4c73', marginBottom: '0.5rem', fontSize: '0.9rem' }}>
                Selected Topics ({profileData.talk_topics.length}):
              </h4>
              <div style={{ display: 'flex', flexWrap: 'wrap', gap: '0.5rem' }}>
                {profileData.talk_topics.map(topicId => {
                  const topic = getTopicInfo(topicId);
                  return (
                    <span key={topicId} style={{
                      background: '#7e5a75',
                      color: '#fff',
                      padding: '0.25rem 0.75rem',
                      borderRadius: '12px',
                      fontSize: '0.8rem',
                      display: 'flex',
                      alignItems: 'center',
                      gap: '0.25rem'
                    }}>
                      {topic.icon} {topic.label}
                    </span>
                  );
                })}
              </div>
            </div>
          )}
        </div>

        {/* Save Button */}
        <div style={{ textAlign: 'center' }}>
          <button
            onClick={handleSave}
            disabled={isSaving}
            style={{
              padding: '1rem 3rem',
              borderRadius: 12,
              border: 'none',
              background: isSaving ? '#ccc' : 'linear-gradient(135deg, #7e5a75 0%, #8a6a7a 100%)',
              color: '#fff',
              fontSize: '1.1rem',
              fontWeight: 600,
              cursor: isSaving ? 'not-allowed' : 'pointer',
              transition: 'all 0.3s ease',
              boxShadow: '0 4px 15px rgba(126,90,117,0.3)'
            }}
          >
            {isSaving ? '💾 Saving...' : '💾 Save Changes'}
          </button>
        </div>
      </div>
    </div>
  );
}

export default Profile;
