import React, { useEffect, useState, createContext, useContext, useRef } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from 'react-router-dom';
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';
import axios from 'axios';

// Translucent backgrounds for sections
const translucentBg = 'rgba(60,76,115,0.06)'; // subtle accent tint
const translucentRose = 'rgba(195,141,148,0.08)'; // subtle rose tint

const UserContext = createContext();
const API = axios.create({ 
  baseURL: process.env.REACT_APP_API_URL || 'http://localhost:3001', 
  withCredentials: true 
});

function useUser() { return useContext(UserContext); }

function Logo() {
  // Placeholder logo: vintage key emoji
  return (
    <span style={{ fontSize: '2.2rem', marginRight: 12, verticalAlign: 'middle' }} role="img" aria-label="BeyondWords Key">üóùÔ∏è</span>
  );
}

function Navbar() {
  const { user, logout } = useUser();
  return (
    <nav style={{ 
      background: '#f5f1ec', 
      padding: '0.8rem 2rem', 
      color: '#3e3e3e', 
      display: 'flex', 
      justifyContent: 'space-between', 
      alignItems: 'center', 
      boxShadow: '0 4px 20px rgba(60,76,115,0.1)',
      position: 'sticky',
      top: 0,
      zIndex: 1000,
      backdropFilter: 'blur(10px)',
      transition: 'all 0.3s ease'
    }}>
      <div style={{ display: 'flex', alignItems: 'center' }}>
        <Logo />
        <Link to="/" style={{ 
          color: '#7e5a75', 
          fontWeight: 500, 
          fontSize: '1.5rem', 
          textDecoration: 'none', 
          letterSpacing: 1,
          fontFamily: 'Grandstander, Arial, sans-serif',
          transition: 'all 0.3s ease',
          ':hover': {
            transform: 'scale(1.05)',
            textShadow: '0 0 20px rgba(126,90,117,0.3)'
          }
        }}>BeyondWords</Link>
      </div>
      <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
        {/* Temporarily hidden - uncomment when MVP is ready
        <Link to="/analyze" style={{ 
          color: '#7e5a75', 
          marginRight: 20,
          textDecoration: 'none',
          padding: '0.4rem 0.8rem',
          borderRadius: 8,
          transition: 'all 0.3s ease',
          ':hover': {
            background: 'rgba(126,90,117,0.1)',
            transform: 'translateY(-2px)'
          }
        }}>Analyze</Link>
        */}
        {user && user.role === 'admin' && <Link to="/admin" style={{ 
          color: '#7e5a75', 
          marginRight: 20,
          textDecoration: 'none',
          padding: '0.4rem 0.8rem',
          borderRadius: 8,
          transition: 'all 0.3s ease',
          ':hover': {
            background: 'rgba(126,90,117,0.1)',
            transform: 'translateY(-2px)'
          }
        }}>Admin</Link>}
        {user ? (
          null
        ) : (
          <Link to="/login" style={{ 
            color: '#7e5a75',
            textDecoration: 'none',
            padding: '0.4rem 0.8rem',
            borderRadius: 8,
            transition: 'all 0.3s ease',
            ':hover': {
              background: 'rgba(126,90,117,0.1)',
              transform: 'translateY(-2px)'
            }
          }}>Login</Link>
        )}
      </div>
    </nav>
  );
}

function Footer() {
  return (
    <footer style={{
      background: 'linear-gradient(135deg, #7e5a75 0%, #8a6a7a 25%, #a67a8a 75%, #c38d94 100%)',
      color: '#f5f1ec',
      padding: '3rem 2rem 2rem 2rem',
      marginTop: '4rem',
      textAlign: 'center',
      position: 'relative',
      overflow: 'hidden'
    }}>
      {/* Animated background elements */}
      <div style={{
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        background: 'radial-gradient(circle at 20% 80%, rgba(245,241,236,0.1) 0%, transparent 50%), radial-gradient(circle at 80% 20%, rgba(195,141,148,0.1) 0%, transparent 50%)',
        animation: 'float 6s ease-in-out infinite'
      }}></div>

      <div style={{ position: 'relative', zIndex: 1 }}>
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', marginBottom: '1rem' }}>
            <span style={{ 
            fontSize: '1.5rem', 
            fontWeight: 500, 
            marginLeft: '0.5rem',
            letterSpacing: 1,
            fontFamily: 'Grandstander, Arial, sans-serif'
          }}>BeyondWords</span>
        </div>

        <p style={{ 
          fontSize: '1rem', 
          marginBottom: '2rem', 
          opacity: 0.9,
          maxWidth: '600px',
          margin: '0 auto 2rem auto',
          lineHeight: 1.6
        }}>
          Our elders are growing older. Family stories are fading. Life's most meaningful moments are unfolding. This is the time to find your voice, before it feels too late.
        </p>

        <div style={{ 
          display: 'flex', 
          justifyContent: 'center', 
          gap: '2rem', 
          marginBottom: '2rem',
          flexWrap: 'wrap'
        }}>

          <a href="#terms" style={{ 
            color: '#f5f1ec', 
            textDecoration: 'none',
            padding: '0.5rem 1rem',
            borderRadius: 6,
            transition: 'all 0.3s ease',
            ':hover': {
              background: 'rgba(245,241,236,0.1)',
              transform: 'translateY(-2px)'
            }
          }}>Join the Waitlist</a>
        </div>

        <div style={{ 
          borderTop: '1px solid rgba(245,241,236,0.2)', 
          paddingTop: '1.5rem',
          fontSize: '0.9rem',
          opacity: 0.8
        }}>
          ¬© 2024 BeyondWords.
        </div>
      </div>
    </footer>
  );
}

function Landing() {
  // Waitlist state
  const [email, setEmail] = useState('');
  const [submitted, setSubmitted] = useState(false);
  const [error, setError] = useState('');
  const featureScrollRef = useRef(null);
  const cardWidth = 340;
  const gap = 32;
  const featureCards = [
    { icon: 'ü§ù', title: 'Conversational Practice', desc: 'Chat naturally with AI judgement-free.' },
    { icon: 'üå±', title: 'Detailed Feedback', desc: 'Get personalized feedback on grammar, pronunciation, and cultural context.' },
    { icon: 'üóùÔ∏è', title: 'Speak On-The-Go', desc: 'Integrate your language into your daily life.' },
    { icon: 'üìö', title: 'Cultural Stories', desc: 'Discover proverbs, folktales, and traditions from your heritage.' },
    { icon: 'üéØ', title: 'Personalized Goals', desc: 'Set your own milestones and track your language journey.' },
  ];
  const [featureIndex, setFeatureIndex] = useState(0);
  const [isTransitioning, setIsTransitioning] = useState(false);
  const numCards = featureCards.length;
  const visibleCount = 3;
  const centerIdx = Math.floor(visibleCount / 2);

  // Create infinite carousel by repeating the array
  const infiniteCards = [...featureCards, ...featureCards, ...featureCards];
  const [currentIndex, setCurrentIndex] = useState(numCards); // Start in the middle set

  const handleWaitlistSubmit = (e) => {
    e.preventDefault();
    setError('');
    if (!email.match(/^[^@\s]+@[^@\s]+\.[^@\s]+$/)) {
      setError('Please enter a valid email address.');
      return;
    }
    setSubmitted(true);
    setEmail('');
  };

  const handleFeatureNav = (dir) => {
    if (isTransitioning) return;
    setIsTransitioning(true);

    if (dir === 'right') {
      setCurrentIndex(prev => prev + 1);
    } else {
      setCurrentIndex(prev => prev - 1);
    }
  };

  const handleTransitionEnd = () => {
    setIsTransitioning(false);

    // Wrap around seamlessly
    if (currentIndex >= numCards * 2) {
      setCurrentIndex(numCards);
    } else if (currentIndex < numCards) {
      setCurrentIndex(numCards * 2 - 1);
    }

    // Update the real feature index
    setFeatureIndex(currentIndex % numCards);
  };

  const getRowTranslate = () => {
    return `translateX(-${(currentIndex - centerIdx) * (cardWidth + gap)}px)`;
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: '#f5f1ec',
      fontFamily: 'Montserrat, Arial, sans-serif',
      fontWeight: 900,
      color: '#3e3e3e',
      padding: 0,
      margin: 0,
    }}>
      <style>{`
        @keyframes fadeInUp {
          0% { opacity: 0; transform: translateY(40px); }
          100% { opacity: 1; transform: translateY(0); }
        }
      `}</style>
      {/* Hero Section */}
      <div style={{
        display: 'flex',
        alignItems: 'stretch',
        justifyContent: 'center',
        margin: '0 auto',
        padding: '5rem 2rem 2rem 2rem',
        gap: 64,
        maxWidth: '100%',
        width: '100%',
        animation: 'fadeInUp 0.8s cubic-bezier(0.4,0,0.2,1) both',
        animationDelay: '0.1s'
      }}>
        <div style={{ 
          flex: 1, 
          minWidth: 320, 
          maxWidth: 600,
          background: translucentBg, 
          borderRadius: 24, 
          boxShadow: '0 2px 12px #3c4c7322', 
          padding: '2.5rem 2rem', 
          display: 'flex', 
          flexDirection: 'column', 
          justifyContent: 'center',
          transition: 'all 0.3s ease',
          ':hover': {
            transform: 'translateY(-5px)',
            boxShadow: '0 8px 25px #3c4c7333'
          },
          animation: 'fadeInUp 0.8s cubic-bezier(0.4,0,0.2,1) both',
          animationDelay: '0.2s'
        }}>
          <h1 style={{ fontSize: '2.8rem', fontWeight: 600, color: '#3c4c73', marginBottom: 18, lineHeight: 1.3, fontFamily: 'Grandstander, Arial, sans-serif' }}>Reconnect with Your Heritage Language</h1>
          <p style={{ fontSize: '1rem', color: '#3e3e3e', lineHeight: 1.9, marginBottom: 32, maxWidth: 500, fontFamily: 'AR One Sans', fontWeight: 400 }}>
            An AI-powered tool designed for heritage speakers and diaspora communities to speak their native language with confidence, emotion, and pride.
          </p>
          <div style={{ display: 'flex', flexDirection: 'column', gap: 16, marginBottom: 32 }}>
            <form id="waitlist" onSubmit={handleWaitlistSubmit} style={{ display: 'flex', alignItems: 'flex-start', gap: 12 }}>
              {submitted ? (
                <div style={{ color: '#3e3e3e', background: '#c38d94', padding: '0.7rem 1.2rem', borderRadius: 10, fontWeight: 600, marginBottom: 8 }}>Thank you for joining the waitlist!</div>
              ) : (
                <>
                  <input
                    type="email"
                    value={email}
                    onChange={e => setEmail(e.target.value)}
                    placeholder="Your email address"
                    style={{
                      padding: '0.8rem 1rem',
                      borderRadius: 8,
                      border: '1.5px solid #c38d94',
                      fontSize: '1rem',
                      fontFamily: 'AR One Sans',
                      background: '#f5f1ec',
                      color: '#3e3e3e',
                      width: '100%',
                      maxWidth: 300,
                    }}
                    required
                  />
                  <button type="submit" style={{
                    background: '#7e5a75',
                    color: '#fff',
                    border: 'none',
                    borderRadius: 8,
                    padding: '0.8rem 2rem',
                    fontWeight: 600,
                    fontSize: '1rem',
                    cursor: 'pointer',
                    boxShadow: '0 2px 8px #7e5a7522',
                    fontFamily: 'AR One Sans',
                    whiteSpace: 'nowrap',
                  }}>Join Waitlist</button>
                </>
              )}
              {error && <div style={{ color: '#b04a2b', fontSize: '0.95rem' }}>{error}</div>}
            </form>
          </div>
        </div>
        <div style={{ flex: 1, minWidth: 320, maxWidth: 400, display: 'flex', alignItems: 'stretch', animation: 'fadeInUp 0.8s cubic-bezier(0.4,0,0.2,1) both', animationDelay: '0.3s' }}>
          <div style={{ 
            background: translucentRose, 
            borderRadius: 24, 
            boxShadow: '0 2px 16px #3c4c7333', 
            padding: '2.5rem 2rem', 
            width: '100%', 
            maxWidth: 400, 
            textAlign: 'center', 
            display: 'flex', 
            flexDirection: 'column', 
            justifyContent: 'center',
            transition: 'all 0.3s ease',
            ':hover': {
              transform: 'translateY(-5px)',
              boxShadow: '0 8px 25px #3c4c7333'
            },
            animation: 'fadeInUp 0.8s cubic-bezier(0.4,0,0.2,1) both',
            animationDelay: '0.4s'
          }}>
            <Logo />
            <h2 style={{ color: '#7e5a75', fontSize: '2rem', margin: '0.5rem 0 1rem 0', fontFamily: 'Grandstander, Arial, sans-serif', fontWeight: 700, letterSpacing: 1 }}>Share Your Experience</h2>
            <p style={{ fontSize: '1rem', color: '#3e3e3e', lineHeight: 1.6, marginBottom: 24, fontFamily: 'AR One Sans', fontWeight: 400 }}>
              Do you understand your heritage language but struggle to speak it confidently?
            </p>
            <a href="https://forms.office.com/Pages/ResponsePage.aspx?id=RncIw6pRT0-Po3Vc1N8ikyownBfAaZ5Gk1xJwTt1Ik1UNVNGUllUMFJWNlBQRFVCQlJHSFZZUzZNWi4u" target="_blank" rel="noopener noreferrer" style={{
              background: '#7e5a75',
              color: '#fff',
              border: 'none',
              borderRadius: 8,
              padding: '0.8rem 2rem',
              fontWeight: 600,
              fontSize: '1rem',
              cursor: 'pointer',
              boxShadow: '0 2px 8px #7e5a7522',
              fontFamily: 'AR One Sans',
              textDecoration: 'none',
              display: 'inline-block',
              transition: 'all 0.3s ease',
              ':hover': {
                transform: 'translateY(-2px)',
                boxShadow: '0 4px 15px #7e5a7555'
              }
            }}>Take Our Survey</a>
          </div>
        </div>
      </div>

      {/* Features Carousel - true smooth cyclic carousel */}
      <div style={{ position: 'relative', margin: '0 auto', marginTop: 32, width: (cardWidth + gap) * visibleCount - gap + 80, minHeight: 260, display: 'flex', flexDirection: 'column', alignItems: 'center', animation: 'fadeInUp 0.8s cubic-bezier(0.4,0,0.2,1) both', animationDelay: '0.5s', overflow: 'visible' }}>
        <button
          aria-label="Scroll left"
          onClick={() => handleFeatureNav('left')}
          style={{
            position: 'absolute',
            left: 0,
            top: '50%',
            transform: 'translateY(-50%)',
            zIndex: 2,
            background: '#fff',
            border: 'none',
            borderRadius: 24,
            boxShadow: '0 2px 8px #3c4c7322',
            width: 40,
            height: 40,
            fontSize: 24,
            color: '#7e5a75',
            cursor: 'pointer',
            opacity: 0.85,
            display: 'block'
          }}
        >
          &#8592;
        </button>
        <div style={{
          width: (cardWidth + gap) * visibleCount - gap,
          height: 220,
          overflow: 'hidden',
          position: 'relative',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
        }}>
          <div
            style={{
              display: 'flex',
              flexDirection: 'row',
              gap: `${gap}px`,
              transition: isTransitioning ? 'transform 0.7s cubic-bezier(0.4,0,0.2,1)' : 'none',
              transform: getRowTranslate(),
              alignItems: 'center',
            }}
            onTransitionEnd={handleTransitionEnd}
          >
            {infiniteCards.map((card, idx) => {
              // Determine if this card is the center, left, or right card
              const isCenter = idx === currentIndex;
              const isLeft = idx === currentIndex - 1;
              const isRight = idx === currentIndex + 1;

              let opacity = 0.3, scale = 0.9, zIndex = 1;
              if (isCenter) {
                opacity = 1;
                scale = 1;
                zIndex = 3;
              } else if (isLeft || isRight) {
                opacity = 0.7;
                scale = 0.92;
                zIndex = 2;
              }

              return (
                <div
                  key={card.title + idx}
                  style={{
                    width: cardWidth,
                    height: 220,
                    background: 'rgba(60,76,115,0.06)',
                    borderRadius: 24,
                    boxShadow: isCenter ? '0 2px 12px #3c4c7322' : '0 1px 4px #3c4c7311',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    transition: 'all 0.7s cubic-bezier(0.4,0,0.2,1)',
                    opacity,
                    scale,
                    zIndex,
                  }}
                >
                  <FeatureCard icon={card.icon} title={card.title} desc={card.desc} noBorder={false} />
                </div>
              );
            })}
          </div>
        </div>
        <button
          aria-label="Scroll right"
          onClick={() => handleFeatureNav('right')}
          style={{
            position: 'absolute',
            right: 0,
            top: '50%',
            transform: 'translateY(-50%)',
            zIndex: 2,
            background: '#fff',
            border: 'none',
            borderRadius: 24,
            boxShadow: '0 2px 8px #3c4c7322',
            width: 40,
            height: 40,
            fontSize: 24,
            color: '#7e5a75',
            cursor: 'pointer',
            opacity: 0.85,
            display: 'block'
          }}
