import React, { useEffect, useState } from 'react';
import { useUser } from './App';
import axios from 'axios';
import { Link } from 'react-router-dom';

const API = axios.create({ 
  baseURL: process.env.REACT_APP_API_URL || 'http://localhost:3001', 
  withCredentials: true 
});

function Dashboard() {
  const { user } = useUser();
  const [sessions, setSessions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    async function fetchSessions() {
      setLoading(true);
      setError('');
      try {
        const res = await API.get(`/api/sessions/${user?.id}`);
        setSessions(res.data.sessions || []);
      } catch (err) {
        setError('Failed to load sessions.');
      } finally {
        setLoading(false);
      }
    }
    if (user?.id) fetchSessions();
  }, [user]);

  // Placeholder streak and usage logic
  const streak = 3; // TODO: calculate real streak from sessions
  const usageByDay = [
    { day: 'Mon', count: 1 },
    { day: 'Tue', count: 2 },
    { day: 'Wed', count: 1 },
    { day: 'Thu', count: 0 },
    { day: 'Fri', count: 2 },
    { day: 'Sat', count: 1 },
    { day: 'Sun', count: 0 },
  ];

  return (
    <div style={{ minHeight: '100vh', background: 'linear-gradient(135deg, #f5f1ec 0%, #e8e0d8 50%, #d4c8c0 100%)', padding: '2rem' }}>
      <div style={{ maxWidth: 700, margin: '0 auto', background: '#fff', borderRadius: 20, boxShadow: '0 8px 32px rgba(60,76,115,0.12)', padding: '2.5rem 2rem', position: 'relative' }}>
        <h1 style={{ color: '#3c4c73', fontFamily: 'Grandstander, Arial, sans-serif', fontWeight: 700, fontSize: '2rem', marginBottom: 24 }}>Welcome, {user?.name || user?.email}!</h1>
        <div style={{ marginBottom: 32 }}>
          <h2 style={{ color: '#7e5a75', fontSize: '1.2rem', fontWeight: 600, marginBottom: 8 }}>🔥 Streak</h2>
          <div style={{ fontSize: '2.5rem', fontWeight: 700, color: '#c38d94', marginBottom: 8 }}>{streak} days</div>
          <div style={{ color: '#7e5a75', fontSize: '1rem', opacity: 0.8 }}>Keep your streak going by practicing every day!</div>
        </div>
        <div style={{ marginBottom: 32 }}>
          <h2 style={{ color: '#7e5a75', fontSize: '1.2rem', fontWeight: 600, marginBottom: 8 }}>📈 Usage This Week</h2>
          <div style={{ display: 'flex', gap: 12, alignItems: 'flex-end', height: 80 }}>
            {usageByDay.map(({ day, count }) => (
              <div key={day} style={{ textAlign: 'center', flex: 1 }}>
                <div style={{ background: '#c38d94', height: count * 20 + 8, width: 18, borderRadius: 6, margin: '0 auto 6px auto', transition: 'height 0.3s' }}></div>
                <div style={{ fontSize: '0.95rem', color: '#3c4c73', opacity: 0.7 }}>{day}</div>
              </div>
            ))}
          </div>
        </div>
        <div>
          <h2 style={{ color: '#7e5a75', fontSize: '1.2rem', fontWeight: 600, marginBottom: 8 }}>💬 Previous Conversations</h2>
          {loading ? (
            <div>Loading...</div>
          ) : error ? (
            <div style={{ color: '#dc3545' }}>{error}</div>
          ) : sessions.length === 0 ? (
            <div style={{ color: '#7e5a75', opacity: 0.7 }}>No previous conversations yet.</div>
          ) : (
            <ul style={{ listStyle: 'none', padding: 0 }}>
              {sessions.map(session => (
                <li key={session.id} style={{ marginBottom: 12, background: '#f8f6f4', borderRadius: 8, padding: '0.8rem 1rem', boxShadow: '0 1px 4px #c38d9422' }}>
                  <div style={{ fontWeight: 600, color: '#3c4c73', marginBottom: 4 }}>Session on {new Date(session.created_at).toLocaleString()}</div>
                  <div style={{ color: '#7e5a75', fontSize: '0.98rem', marginBottom: 4, opacity: 0.85 }}>
                    {session.chat_history && session.chat_history.length > 0 ? (
                      <span>Messages: {session.chat_history.length}</span>
                    ) : (
                      <span>No messages</span>
                    )}
                  </div>
                  {/* Placeholder for link to view session */}
                  <Link to="/analyze" style={{ color: '#c38d94', fontWeight: 600, textDecoration: 'none', fontSize: '0.97rem' }}>View</Link>
                </li>
              ))}
            </ul>
          )}
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
