// Add localStorage persistence for chatHistory
function usePersistentChatHistory(user) {
  const [chatHistory, setChatHistory] = useState(() => {
    if (!user) {
      const saved = localStorage.getItem('chatHistory');
      return saved ? JSON.parse(saved) : [];
    }
    return [];
  });

  useEffect(() => {
    if (!user) {
      localStorage.setItem('chatHistory', JSON.stringify(chatHistory));
    } else {
      localStorage.removeItem('chatHistory');
    }
  }, [chatHistory, user]);

  return [chatHistory, setChatHistory];
}

function Admin() {
  const { user } = useUser();
  if (!user || user.role !== 'admin') return <Navigate to="/" />;
  return <div style={{ padding: '2rem' }}>Admin Page (Edit conversational features here)</div>;
}

function Login() {
  const { setUser } = useUser();

  const handleSuccess = async (credentialResponse) => {
    try {
      // Send the credential to your backend
      const response = await API.post('/auth/google/token', {
        credential: credentialResponse.credential
      });

      if (response.data.user) {
        setUser(response.data.user);
        window.location.href = '/';
      }
    } catch (error) {
      console.error('Login error:', error);
    }
  };

  const handleError = () => {
    console.error('Login Failed');
  };

  return (
    <div style={{ padding: '4rem', textAlign: 'center' }}>
      <h2>Login with Google</h2>
      <GoogleLogin
        onSuccess={handleSuccess}
        onError={handleError}
        useOneTap
      />
    </div>
  );
}

function App() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    API.get('/api/user').then(res => setUser(res.data)).catch(() => setUser(null));
  }, []);

  const logout = async () => {
    await API.get('/api/logout');
    setUser(null);
    window.location.href = '/';
  };

  return (
    <GoogleOAuthProvider clientId="YOUR_GOOGLE_CLIENT_ID">
      <UserContext.Provider value={{ user, setUser, logout }}>
        <Router>
          <Navbar />
          <Routes>
            <Route path="/" element={<Landing />} />
            <Route path="/analyze" element={<Analyze />} />
            <Route path="/admin" element={<Admin />} />
            <Route path="/login" element={<Login />} />
          </Routes>
        </Router>
      </UserContext.Provider>
    </GoogleOAuthProvider>
  );
}

