import React, { useState, useEffect } from 'react';
import { useUser } from './App';
import axios from 'axios';


const API = axios.create({ 
  baseURL: process.env.REACT_APP_API_URL || 'http://localhost:4000', 
  withCredentials: false 
});

API.interceptors.request.use(config => {
  const token = localStorage.getItem('jwt');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

function Profile() {
  const { user, setUser } = useUser();
  const [isLoading, setIsLoading] = useState(false);
  const [isSaving, setIsSaving] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // Form states
  const [profileData, setProfileData] = useState({
    name: '',
    email: ''
  });

  useEffect(() => {
    if (user) {
      console.log('Setting profile data from user:', user);
      setProfileData({
        name: user.name || '',
        email: user.email || ''
      });
    }
  }, [user]);

  const handleInputChange = (field, value) => {
    setProfileData(prev => ({
      ...prev,
      [field]: value
    }));
    setError('');
    setSuccess('');
  };

  const toggleTopic = (topicId) => {
    setProfileData(prev => ({
      ...prev,
      talk_topics: prev.talk_topics.includes(topicId)
        ? prev.talk_topics.filter(id => id !== topicId)
        : [...prev.talk_topics, topicId]
    }));
    setError('');
    setSuccess('');
  };

  const handleSave = async () => {
    setIsSaving(true);
    setError('');
    setSuccess('');

    console.log('Saving profile data:', profileData);
    console.log('Current user:', user);

    // Basic validation
    if (!profileData.name || !profileData.email) {
      setError('Please fill in all required fields');
      setIsSaving(false);
      return;
    }

    try {
      const requestData = {
        name: profileData.name,
        email: profileData.email
      };

      console.log('Making PUT request with data:', requestData);

      const response = await API.put('/api/user/profile', requestData);

      console.log('Profile update response:', response.data);
      setUser(response.data.user);
      setSuccess('Profile updated successfully!');
    } catch (err) {
      console.error('Error updating profile:', err);
      console.error('Error response:', err.response?.data);
      console.error('Error status:', err.response?.status);
      setError(err.response?.data?.error || 'Failed to update profile. Please try again.');
    } finally {
      setIsSaving(false);
    }
  };

  const getLanguageInfo = (code) => {
    return LANGUAGES.find(lang => lang.code === code) || { label: code, flag: 'üåç' };
  };

  const getProficiencyInfo = (level) => {
    return PROFICIENCY_LEVELS.find(p => p.level === level) || { label: level, icon: 'üå±' };
  };

  const getTopicInfo = (id) => {
    return TALK_TOPICS.find(topic => topic.id === id) || { label: id, icon: 'üí≠' };
  };

  if (!user) {
    return (
      <div style={{ 
        minHeight: '100vh', 
        display: 'flex', 
        alignItems: 'center', 
        justifyContent: 'center',
        background: 'linear-gradient(135deg, #f5f1ec 0%, #e8e0d8 50%, #d4c8c0 100%)'
      }}>
        <div style={{ color: '#7e5a75', fontSize: '1.2rem' }}>Please log in to view your profile.</div>
      </div>
    );
  }

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #f5f1ec 0%, #e8e0d8 50%, #d4c8c0 100%)',
      padding: '2rem'
    }}>
      <div style={{ maxWidth: 800, margin: '0 auto' }}>
        {/* Header */}
        <div style={{
          background: '#fff',
          borderRadius: 20,
          boxShadow: '0 8px 32px rgba(60,76,115,0.12)',
          padding: '2.5rem 2rem',
          marginBottom: '2rem'
        }}>
          <h1 style={{
            color: '#3c4c73',
            fontFamily: 'Grandstander, Arial, sans-serif',
            fontWeight: 700,
            fontSize: '2rem',
            marginBottom: '0.5rem'
          }}>
            ‚öôÔ∏è Profile Settings
          </h1>
          <p style={{ color: '#7e5a75', fontSize: '1rem', margin: 0 }}>
            Manage your account information and learning preferences
          </p>
        </div>

        {/* Messages */}
        {error && (
          <div style={{
            background: 'rgba(220,53,69,0.1)',
            color: '#dc3545',
            padding: '1rem',
            borderRadius: 12,
            marginBottom: '2rem',
            border: '1px solid rgba(220,53,69,0.2)'
          }}>
            {error}
          </div>
        )}

        {success && (
          <div style={{
            background: 'rgba(40,167,69,0.1)',
            color: '#28a745',
            padding: '1rem',
            borderRadius: 12,
            marginBottom: '2rem',
            border: '1px solid rgba(40,167,69,0.2)'
          }}>
            {success}
          </div>
        )}

        {/* Personal Information */}
        <div style={{
          background: '#fff',
          borderRadius: 16,
          boxShadow: '0 4px 20px rgba(60,76,115,0.1)',
          padding: '2rem',
          marginBottom: '2rem'
        }}>
          <h2 style={{
            color: '#3c4c73',
            fontSize: '1.3rem',
            fontWeight: 600,
            marginBottom: '1.5rem',
            display: 'flex',
            alignItems: 'center',
            gap: '0.5rem'
          }}>
            üë§ Personal Information
          </h2>

          <div style={{ display: 'grid', gap: '1.5rem' }}>
            <div>
              <label style={{ 
                display: 'block', 
                color: '#3c4c73', 
                fontWeight: 600, 
                marginBottom: '0.5rem' 
              }}>
                Full Name
              </label>
              <input
                type="text"
                value={profileData.name}
                onChange={(e) => handleInputChange('name', e.target.value)}
                style={{
                  width: '100%',
                  padding: '0.75rem',
                  borderRadius: '8px',
                  border: '2px solid rgba(126,90,117,0.2)',
                  fontSize: '1rem',
                  fontFamily: 'inherit',
                  background: '#f8f6f4',
                  color: '#3c4c73'
                }}
              />
            </div>

            <div>
              <label style={{ 
                display: 'block', 
                color: '#3c4c73', 
                fontWeight: 600, 
                marginBottom: '0.5rem' 
              }}>
                Email Address
              </label>
              <input
                type="email"
                value={profileData.email}
                onChange={(e) => handleInputChange('email', e.target.value)}
                style={{
                  width: '100%',
                  padding: '0.75rem',
                  borderRadius: '8px',
                  border: '2px solid rgba(126,90,117,0.2)',
                  fontSize: '1rem',
                  fontFamily: 'inherit',
                  background: '#f8f6f4',
                  color: '#3c4c73'
                }}
              />
            </div>
          </div>
        </div>

        {/* Language Dashboard Info */}
        <div style={{
          background: '#fff',
          borderRadius: 16,
          boxShadow: '0 4px 20px rgba(60,76,115,0.1)',
          padding: '2rem',
          marginBottom: '2rem'
        }}>
          <h2 style={{
            color: '#3c4c73',
